<template>
  <div class="upload_files" @click="$refs.mdUploadFile.click()">
    <span class="icon iconfont icon-tupian"></span>
    <input
      ref="mdUploadFile"
      class="md_upload"
      type="file"
      name="md-upload-file"
      id="md-upload-file"
      @change="upload"
      multiple
    />
    <span>添加附件</span>
  </div>
</template>
<script>
import { mapState, mapMutations } from "vuex";
import { formatText } from "@/assets/js/utils";
export default {
  data() {
    return {};
  },
  computed: {
    ...mapState(["text", "selectionInfo"])
  },
  methods: {
    ...mapMutations(["setText"]),
    upload(e) {
      const fileList = Array.from(e.target.files);
      console.log(fileList);
      fileList.forEach(item => {
        console.log(item);
        // const formData = new formData();
        // formData.append("file", item);
        this.updateText(
          "![",
          "](data:image/jpeg;base64,)"
        );
      });
    },
    updateText(startStr, endStr) {
      const selectionInfo = this.selectionInfo;
      const originalText = this.text;
      const newText = formatText(originalText, selectionInfo, startStr, endStr);
      if (!newText) return;
      this.setText(newText);
    }
  }
};
</script>
